                    case LEFT:
                        return PipePatterns.Unfilled.RIGHT_ARROW;
                    case RIGHT:
                        return PipePatterns.Unfilled.LEFT_ARROW;
                }
            }
        }
        return '\0'; // Default return value if none of the conditions are met
