     */
    @Override
    public char toSingleChar() {
        char character;
        if (isFilled) {
            switch (pointingTo) {
                case UP:
                    character = PipePatterns.Filled.UP_ARROW;
                    break;
                case DOWN:
                    character = PipePatterns.Filled.DOWN_ARROW;
                    break;
                case LEFT:
                    character = PipePatterns.Filled.LEFT_ARROW;
                    break;
                case RIGHT:
                    character = PipePatterns.Filled.RIGHT_ARROW;
                    break;
                default:
                    character = '?'; // Should never happen
            }
        } else {
