                return new Pipe(Shape.BOTTOM_RIGHT);
            case "CR":
                return new Pipe(Shape.CROSS);
            default:
                throw new IllegalArgumentException("Unknown pipe representation: " + rep);
        }
    }

    public enum Shape {
