package game.pipes;

import game.MapElement;
import util.Direction;
import util.PipePatterns;

import java.util.HashMap;
import java.util.Map;

public class Pipe implements MapElement {

    private final Shape shape;
    private boolean filled = false;

    public Pipe(Shape shape) {
        this.shape = shape;
    }

    public void setFilled() {
        this.filled = true;
    }

    public boolean getFilled() {
        return this.filled;
    }

    public Direction[] getConnections() {
        switch (this.shape) {
            case HORIZONTAL:
                return new Direction[]{Direction.LEFT, Direction.RIGHT};
            case VERTICAL:
                return new Direction[]{Direction.UP, Direction.DOWN};
            case TOP_LEFT:
                return new Direction[]{Direction.UP, Direction.LEFT};
            case TOP_RIGHT:
                return new Direction[]{Direction.UP, Direction.RIGHT};
            case BOTTOM_LEFT:
                return new Direction[]{Direction.DOWN, Direction.LEFT};
            case BOTTOM_RIGHT:
                return new Direction[]{Direction.DOWN, Direction.RIGHT};
            case CROSS:
                return Direction.values();
            default:
                throw new IllegalStateException("Unexpected value: " + this.shape);
        }
    }

    @Override
    public char toSingleChar() {
        return this.shape.getCharByState(this.filled);
    }

    public static Pipe fromString(String rep) {
        Map<String, Shape> stringToShape = new HashMap<>();
        stringToShape.put("HZ", Shape.HORIZONTAL);
        stringToShape.put("VT", Shape.VERTICAL);
        stringToShape.put("TL", Shape.TOP_LEFT);
        stringToShape.put("TR", Shape.TOP_RIGHT);
        stringToShape.put("BL", Shape.BOTTOM_LEFT);
        stringToShape.put("BR", Shape.BOTTOM_RIGHT);
        stringToShape.put("CR", Shape.CROSS);

        Shape shape = stringToShape.get(rep);
        if (shape == null) {
            throw new IllegalArgumentException("Unknown pipe representation: " + rep);
        }
        return new Pipe(shape);
    }

    public enum Shape {
        HORIZONTAL(PipePatterns.Filled.HORIZONTAL, PipePatterns.Unfilled.HORIZONTAL),
        VERTICAL(PipePatterns.Filled.VERTICAL, PipePatterns.Unfilled.VERTICAL),
        TOP_LEFT(PipePatterns.Filled.TOP_LEFT, PipePatterns.Unfilled.TOP_LEFT),
        TOP_RIGHT(PipePatterns.Filled.TOP_RIGHT, PipePatterns.Unfilled.TOP_RIGHT),
        BOTTOM_LEFT(PipePatterns.Filled.BOTTOM_LEFT, PipePatterns.Unfilled.BOTTOM_LEFT),
