import game.map.cells.Cell;
import game.map.cells.FillableCell;
import game.pipes.Pipe;
import io.Deserializer;
import util.Coordinate;
import game.DelayBar;
import game.CellStack;
import game.PipeQueue;

import java.util.List;

public class Game {

    private final Map map;
    private final PipeQueue pipeQueue;
    private final DelayBar delayBar;
    private final CellStack cellStack = new CellStack();

    private int numOfSteps = 0;

    public Game(int rows, int cols) {
        this.map = new Map(rows, cols);
        this.pipeQueue = new PipeQueue();
        this.delayBar = new DelayBar(5); // Assuming a default delay value
    }

    public Game(int rows, int cols, int delay, Cell[][] cells, List<Pipe> pipes) {
        this.map = new Map(rows, cols, cells);
        this.pipeQueue = new PipeQueue(pipes);
        this.delayBar = new DelayBar(delay);
    }

    static Game fromString(int rows, int cols, int delay, String cellsRep, List<Pipe> pipes) {
        Cell[][] cells = Deserializer.parseString(rows, cols, cellsRep);
        return new Game(rows, cols, delay, cells, pipes);
    }

    public boolean placePipe(int row, char col) {
        Coordinate coord = new Coordinate(row - 1, col - 'A');
        Pipe pipe = pipeQueue.peek();
        if (map.tryPlacePipe(coord, pipe)) {
            pipeQueue.consume();
            delayBar.countdown();
            cellStack.push((FillableCell) map.cells[coord.row][coord.col]);
            numOfSteps++;
            return true;
        }
        return false;
    }

    public void skipPipe() {
        pipeQueue.consume();
        numOfSteps++;
    }

    public boolean undoStep() {
        if (cellStack.getUndoCount() > 0) {
            FillableCell cell = cellStack.pop();
            map.undo(cell.coord);
            numOfSteps++;
            return true;
        }
        return false;
    }

    public void display() {
        map.display();
        System.out.println();
        pipeQueue.display();
        cellStack.display();
        System.out.println();
        delayBar.display();
    }

    public void updateState() {
        int distance = delayBar.distance();
        map.fillTiles(distance);
    }

    public boolean hasWon() {
        return map.checkPath();
    }

    public boolean hasLost() {
        return map.hasLost() || delayBar.distance() < 0;
    }

    public int getNumOfSteps() {
        return numOfSteps;
    }
}
```

