     *
     * @param game   the game object
     * @param source the current place of the piece
     * @return an array of available moves
     */
    @Override
    public Move[] getAvailableMoves(Game game, Place source) {
        List<Move> moves = new ArrayList<>();
        int x = source.x();
        int y = source.y();

        // Check up
        for (int i = x - 1; i >= 0; i--) {
            if (game.getPiece(i, y) != null) {
                if (game.getPiece(i, y).getPlayer() != this.getPlayer()) {
                    moves.add(new Move(source, i, y));
                }
                break;
            }
        }

        // Check down
        for (int i = x + 1; i < game.getSize(); i++) {
            if (game.getPiece(i, y) != null) {
                if (game.getPiece(i, y).getPlayer() != this.getPlayer()) {
                    moves.add(new Move(source, i, y));
                }
                break;
            }
        }

        // Check left
        for (int i = y - 1; i >= 0; i--) {
            if (game.getPiece(x, i) != null) {
                if (game.getPiece(x, i).getPlayer() != this.getPlayer()) {
                    moves.add(new Move(source, x, i));
                }
                break;
            }
        }

        // Check right
        for (int i = y + 1; i < game.getSize(); i++) {
            if (game.getPiece(x, i) != null) {
                if (game.getPiece(x, i).getPlayer() != this.getPlayer()) {
                    moves.add(new Move(source, x, i));
                }
                break;
            }
        }

        return moves.toArray(new Move[0]);
    }
}

