        this.board = configuration.getInitialBoard();
        this.currentPlayer = null;
        this.refreshOutput();
        while (true) {
            for (Player player : configuration.getPlayers()) {
                this.currentPlayer = player;
                Move[] availableMoves = this.getAvailableMoves(player);
                if (availableMoves.length == 0) {
                    winner = this.getPlayers()[(player.getColor() == Color.BLACK) ? 1 : 0];
                    break;
                }
                Move move = player.nextMove(this, availableMoves);
                this.movePiece(move);
                this.updateScore(player, this.getPiece(move.getDestination()), move);
                this.refreshOutput();
                winner = this.getWinner(player, this.getPiece(move.getDestination()), move);
                if (winner != null) {
                    break;
                }
            }
            if (winner != null) {
                System.out.println();
                System.out.println("Congratulations! ");
                System.out.printf("Winner: %s%s%s\n", winner.getColor(), winner.getName(), Color.DEFAULT);
                return winner;
            }
        }
    }

    @Override
    public Player getWinner(Player lastPlayer, Piece lastPiece, Move lastMove) {
        if (lastPiece instanceof Knight) {
            return lastPlayer;
        }
        return null;
    }

    public void updateScore(Player player, Piece piece, Move move) {
        int score = player.getScore();
        score += Math.abs(move.getSource().x() - move.getDestination().x()) + Math.abs(move.getSource().y() - move.getDestination().y());
        player.setScore(score);
    }

    public void movePiece(Move move) {
        this.board[move.getDestination().x()][move.getDestination().y()] = this.board[move.getSource().x()][move.getSource().y()];
        this.board[move.getSource().x()][move.getSource().y()] = null;
    }

    public Move[] getAvailableMoves(Player player) {
        List<Move> availableMoves = new ArrayList<>();
        for (Piece[] pieces : this.board) {
            for (Piece piece : pieces) {
                if (piece != null && piece.getPlayer().equals(player)) {
                    availableMoves.addAll(Arrays.asList(piece.getAvailableMoves(this, piece.getPlayer().getColor() == Color.BLACK ? this.getCentralPlace() : new Place(0, 0))));
                }
            }
        }
        return availableMoves.toArray(new Move[0]);
    }
