```java
package assignment;

import assignment.protocol.*;

public class JesonMor extends Game {
    public JesonMor(Configuration configuration) {
        super(configuration);
    }

    @Override
    public Player start() {
        Player winner = null;
        this.numMoves = 0;
        this.board = configuration.getInitialBoard();
        this.currentPlayer = null;
        this.refreshOutput();
        while (true) {
            for (Player player : configuration.getPlayers()) {
                this.currentPlayer = player;
                Move[] availableMoves = this.getAvailableMoves(player);
                if (availableMoves.length == 0) {
                    winner = player;
                    break;
                }
                Move move = player.nextMove(this, availableMoves);
                Piece piece = this.getPiece(move.getSource());
                this.movePiece(move);
                this.updateScore(player, piece, move);
                this.refreshOutput();
            }
            if (winner != null) {
                System.out.println();
                System.out.println("Congratulations! ");
                System.out.printf("Winner: %s%s%s\n", winner.getColor(), winner.getName(), Color.DEFAULT);
                return winner;
            }
        }
    }

    @Override
    public Player getWinner(Player lastPlayer, Piece lastPiece, Move lastMove) {
        return lastPlayer;
    }

    @Override
    public void updateScore(Player player, Piece piece, Move move) {
        int score = player.getScore();
        score += Math.abs(move.getSource().x() - move.getDestination().x()) + Math.abs(move.getSource().y() - move.getDestination().y());
        player.setScore(score);
    }

    @Override
    public void movePiece(Move move) {
        Piece piece = this.getPiece(move.getSource());
        this.board[move.getSource().x()][move.getSource().y()] = null;
        this.board[move.getDestination().x()][move.getDestination().y()] = piece;
    }

    @Override
    public Move[] getAvailableMoves(Player player) {
        Move[] availableMoves = new Move[0];
        for (Piece[] pieces : this.board) {
            for (Piece piece : pieces) {
                if (piece != null && piece.getPlayer().equals(player)) {
                    Move[] moves = piece.getAvailableMoves(this, piece.getPlayer().getColor().equals(Color.BLACK) ? new Place(piece.getPlayer().getName().equals("Player1") ? 0 : this.configuration.getSize() - 1, piece.getPlayer().getName().equals("Player1") ? 0 : this.configuration.getSize() - 1) : new Place(piece.getPlayer().getName().equals("Player1") ? this.configuration.getSize() - 1 : 0, piece.getPlayer().getName().equals("Player1") ? this.configuration.getSize() - 1 : 0));
                    Move[] temp = new Move[availableMoves.length + moves.length];
                    System.arraycopy(availableMoves, 0, temp, 0, availableMoves.length);
                    System.arraycopy(moves, 0, temp, availableMoves.length, moves.length);
                    availableMoves = temp;
                }
            }
        }
        return availableMoves;
    }
}
