    @Override
    public Move[] getAvailableMoves(Game game, Place source) {
        List<Move> moves = new ArrayList<>();
        int x = source.x();
        int y = source.y();

        // Check up
        for (int i = y - 1; i >= 0; i--) {
            Place destination = new Place(x, i);
            Move move = new Move(source, destination);
            if (game.getPiece(destination) != null) {
                if (game.getPiece(destination).getPlayer() != this.getPlayer()) {
                    moves.add(move);
                }
                break;
            } else {
                moves.add(move);
            }
        }

        // Check down
        for (int i = y + 1; i < game.getSize(); i++) {
            Place destination = new Place(x, i);
            Move move = new Move(source, destination);
            if (game.getPiece(destination) != null) {
                if (game.getPiece(destination).getPlayer() != this.getPlayer()) {
                    moves.add(move);
                }
                break;
            } else {
                moves.add(move);
            }
        }

        // Check left
        for (int i = x - 1; i >= 0; i--) {
            Place destination = new Place(i, y);
            Move move = new Move(source, destination);
            if (game.getPiece(destination) != null) {
                if (game.getPiece(destination).getPlayer() != this.getPlayer()) {
                    moves.add(move);
                }
                break;
            } else {
                moves.add(move);
            }
        }

        // Check right
        for (int i = x + 1; i < game.getSize(); i++) {
            Place destination = new Place(i, y);
            Move move = new Move(source, destination);
            if (game.getPiece(destination) != null) {
