        Player winner = null;
        while (winner == null) {
            Move[] availableMoves = getAvailableMoves(currentPlayer);
            Move nextMove = currentPlayer.nextMove(this, availableMoves);
            movePiece(nextMove);
            updateScore(currentPlayer, getPiece(nextMove.getDestination()), nextMove);
            winner = getWinner(currentPlayer, getPiece(nextMove.getDestination()), nextMove);
            refreshOutput();
            currentPlayer = getNextPlayer();
        }
        return winner;
    }

    @Override
    public Player getWinner(Player lastPlayer, Piece lastPiece, Move lastMove) {
        if (lastMove.getDestination().x() == configuration.getCentralPlace().x() &&
                lastMove.getDestination().y() == configuration.getCentralPlace().y()) {
            return lastPlayer;
        }
        return null;
    }

    @Override
    public void updateScore(Player player, Piece piece, Move move) {
        int score = player.getScore() + Math.abs(move.getDestination().x() - move.getSource().x()) +
                Math.abs(move.getDestination().y() - move.getSource().y());
        player.setScore(score);
    }

    @Override
    public void movePiece(Move move) {
        board[move.getDestination().x()][move.getDestination().y()] = board[move.getSource().x()][move.getSource().y()];
        board[move.getSource().x()][move.getSource().y()] = null;
    }

    @Override
    public Move[] getAvailableMoves(Player player) {
        Move[] availableMoves = new Move[0];
        for (int i = 0; i < configuration.getSize(); i++) {
