                    System.arraycopy(pieceMoves, 0, temp, availableMoves.length, pieceMoves.length);
                    availableMoves = temp;
                }
            }
        }
        return availableMoves;
    }

    private Player getNextPlayer() {
        for (int i = 0; i < configuration.getPlayers().length; i++) {
            if (configuration.getPlayers()[i].equals(currentPlayer)) {
                return configuration.getPlayers()[(i + 1) % configuration.getPlayers().length];
            }
        }
        return null;
    }
