package assignment.piece;

import assignment.piece.Rule;
import assignment.protocol.Configuration;
import assignment.protocol.Game;
import assignment.protocol.Move;
import assignment.protocol.Place;

/**
 * Global rule that requires the source and destination should be inside the board boundary.
 */
public class OutOfBoundaryRule implements Rule {
    @Override
    public boolean validate(Game game, Move move) {
        Configuration configuration = game.getConfiguration();
        Place source = move.getSource();
        Place destination = move.getDestination();

        int size = configuration.getSize();

        if (source.x() < 0 || source.x() >= size || source.y() < 0 || source.y() >= size) {
            return false;
        }

        if (destination.x() < 0 || destination.x() >= size || destination.y() < 0 || destination.y() >= size) {
            return false;
        }

        return true;
    }

