```java
package pa1.model;

import java.util.*;

public class GameBoard {

    private final int numRows;
    private final int numCols;
    private final Cell[][] board;
    private final Player player;
    private int numGems;

    public GameBoard(final int numRows, final int numCols, final Cell[][] cells) {
        this.numRows = numRows;
        this.numCols = numCols;
        this.board = cells;
        this.player = findPlayer();
        this.numGems = countGems();

        if (this.player == null) {
            throw new IllegalArgumentException("No player found on the game board");
        }

        if (this.numGems == 0) {
            throw new IllegalArgumentException("No gems found on the game board");
        }

        if (!isReachable()) {
            throw new IllegalArgumentException("Some gems are not reachable by the player");
        }
    }

    public Cell[] getRow(final int row) {
        return board[row];
    }

    public Cell[] getCol(final int col) {
        Cell[] column = new Cell[numRows];
        for (int i = 0; i < numRows; i++) {
            column[i] = board[i][col];
        }
        return column;
    }

    public Cell getCell(final int row, final int col) {
        return board[row][col];
    }

    public Cell getCell(final Position position) {
        return board[position.row()][position.col()];
    }

    public EntityCell getEntityCell(final int row, final int col) {
        if (!(board[row][col] instanceof EntityCell)) {
            throw new IllegalArgumentException("The cell at the specified position is not an instance of EntityCell");
        }
        return (EntityCell) board[row][col];
    }

    public EntityCell getEntityCell(final Position position) {
        return getEntityCell(position.row(), position.col());
    }

    public int getNumRows() {
        return numRows;
    }

    public int getNumCols() {
        return numCols;
    }

    public Player getPlayer() {
        return player;
    }

    public int getNumGems() {
        return numGems;
    }

    private Player findPlayer() {
        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                if (board[i][j] instanceof EntityCell) {
                    Entity entity = ((EntityCell) board[i][j]).getEntity();
                    if (entity instanceof Player) {
                        return (Player) entity;
                    }
                }
            }
        }
        return null;
    }

    private int countGems() {
        int count = 0;
        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                if (board[i][j] instanceof EntityCell) {
                    Entity entity = ((EntityCell) board[i][j]).getEntity();
                    if (entity instanceof Gem) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

    private boolean isReachable() {
        boolean[][] visited = new boolean[numRows][numCols];
        Queue<Position> queue = new LinkedList<>();
        queue.add(player.getOwner().getPosition());

        while (!queue.isEmpty()) {
            Position current = queue.poll();
            visited[current.row()][current.col()] = true;

            for (Direction direction : Direction.values()) {
                Position next = current.offsetBy(direction.getOffset());
                if (isValid(next) && !visited[next.row()][next.col()] && !(board[next.row()][next.col()] instanceof Wall)) {
                    queue.add(next);
                }
            }
        }

        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                if (board[i][j] instanceof EntityCell) {
                    Entity entity = ((EntityCell) board[i][j]).getEntity();
                    if (entity instanceof Gem && !visited[i][j]) {
                        return false;
                    }
                }
            }
        }

        return true;
    }

    private boolean isValid(Position position) {
        return position.row() >= 0 && position.row() < numRows && position.col() >= 0 && position.col() < numCols;
    }
}
