                    }
                    player = (Player) cells[row][col];
                } else if (cells[row][col] instanceof Gem) {
                    gemCount++;
                }
            }
        }

        if (player == null) {
            throw new IllegalArgumentException("No player found in the cells.");
        }

        if (gemCount == 0) {
            throw new IllegalArgumentException("No gems found in the cells.");
