        return numGems;
    }

    private Player findPlayer(Cell[][] cells) {
        for (Cell[] row : cells) {
            for (Cell cell : row) {
                if (cell instanceof EntityCell) {
                    Entity entity = ((EntityCell) cell).getEntity();
                    if (entity instanceof Player) {
                        return (Player) entity;
                    }
                }
            }
        }
        return null;
    }

    private int countGems(Cell[][] cells) {
        int count = 0;
        for (Cell[] row : cells) {
            for (Cell cell : row) {
                if (cell instanceof EntityCell) {
                    Entity entity = ((EntityCell) cell).getEntity();
                    if (entity instanceof Gem) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

    private boolean isReachable(Position position, Cell[][] cells) {
        Queue<Position> queue = new LinkedList<>();
        boolean[][] visited = new boolean[numRows][numCols];

        queue.add(position);
        visited[position.row()][position.col()] = true;

        while (!queue.isEmpty()) {
            Position current = queue.poll();
            for (Direction direction : Direction.values()) {
                Position next = current.offsetBy(direction.getOffset());
                if (next.row() >= 0 && next.row() < numRows && next.col() >= 0 && next.col() < numCols
                        && !visited[next.row()][next.col()] && cells[next.row()][next.col()] instanceof EntityCell) {
                    Entity entity = ((EntityCell) cells[next.row()][next.col()]).getEntity();
                    if (entity instanceof Gem) {
                        numGems--;
                    }
                    queue.add(next);
                    visited[next.row()][next.col()] = true;
                }
            }
        }

        return numGems == 0;
    }
