package pa1.model;

import java.util.*;

public class GameBoard {

    private final int numRows;
    private final int numCols;
    private final Cell[][] board;
    private final Player player;
    private int numGems;

    public GameBoard(final int numRows, final int numCols, final Cell[][] cells) {
        this.numRows = numRows;
        this.numCols = numCols;
        this.board = cells;
        this.player = findPlayer(cells);
        this.numGems = countGems(cells);

        if (this.player == null) {
            throw new IllegalArgumentException("There is no player in the cells");
        }

        if (this.numGems == 0) {
            throw new IllegalArgumentException("There are no gems in the cells");
        }

        if (!isReachable(this.player.getOwner())) {
            throw new IllegalArgumentException("There are some gems which cannot be reached by the player");
        }
    }

    public Cell[] getRow(final int row) {
        return board[row];
    }

    public Cell[] getCol(final int col) {
        Cell[] column = new Cell[numRows];
        for (int i = 0; i < numRows; i++) {
            column[i] = board[i][col];
        }
        return column;
    }

    public Cell getCell(final int row, final int col) {
        return board[row][col];
    }

    public Cell getCell(final Position position) {
        return board[position.row()][position.col()];
    }

    public EntityCell getEntityCell(final int row, final int col) {
        Cell cell = getCell(row, col);
        if (cell instanceof EntityCell) {
            return (EntityCell) cell;
        } else {
            throw new IllegalArgumentException("The cell at the specified position is not an instance of EntityCell");
        }
    }

    public EntityCell getEntityCell(final Position position) {
        return getEntityCell(position.row(), position.col());
    }

    public int getNumRows() {
        return numRows;
    }

    public int getNumCols() {
        return numCols;
    }

    public Player getPlayer() {
        return player;
    }

    public int getNumGems() {
        return numGems;
    }

    private Player findPlayer(Cell[][] cells) {
        for (Cell[] row : cells) {
            for (Cell cell : row) {
                if (cell instanceof EntityCell) {
                    Entity entity = ((EntityCell) cell).getEntity();
                    if (entity instanceof Player) {
                        return (Player) entity;
                    }
                }
            }
        }
        return null;
    }

    private int countGems(Cell[][] cells) {
        int count = 0;
        for (Cell[] row : cells) {
            for (Cell cell : row) {
                if (cell instanceof EntityCell) {
                    Entity entity = ((EntityCell) cell).getEntity();
                    if (entity instanceof Gem) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

    private boolean isReachable(EntityCell start) {
        boolean[][] visited = new boolean[numRows][numCols];
        Queue<EntityCell> queue = new LinkedList<>();
        queue.add(start);

        while (!queue.isEmpty()) {
            EntityCell current = queue.poll();
            Position position = current.getPosition();
            int row = position.row();
            int col = position.col();

            if (visited[row][col]) {
                continue;
            }

            visited[row][col] = true;

            for (Direction direction : Direction.values()) {
                PositionOffset offset = direction.getOffset();
                Position neighborPosition = position.offsetBy(offset);

                if (neighborPosition.row() < 0 || neighborPosition.row() >= numRows ||
                        neighborPosition.col() < 0 || neighborPosition.col() >= numCols) {
                    continue;
                }

                Cell neighborCell = getCell(neighborPosition);

                if (neighborCell instanceof EntityCell) {
                    Entity entity = ((EntityCell) neighborCell).getEntity();
                    if (entity instanceof Gem) {
                        numGems--;
                    }
                    queue.add((EntityCell) neighborCell);
                }
            }
        }

        return numGems == 0;
    }
}
