                } else if (entity instanceof Box) {
                    throw new UnsupportedOperationException();
                } else if (entity instanceof Player) {
                    throw new UnsupportedOperationException();
                } else if (entity instanceof Empty) {
                    throw new UnsupportedOperationException();
                } else if (entity == null) {
                    charToPrint = ' ';
                }
                builder.append(charToPrint);
            }
            builder.append('\n');
