import java.util.stream.Collectors;

public class GameState {

    private final Stack<Transition> history = new Stack<>();
    private final Map<Position, Entity> entities = new HashMap<>();
    private final int boardWidth;
    private final int boardHeight;
    private final Set<Position> destinations;
    private int undoQuota;
    private Transition currentTransition = new Transition();

    public GameState(GameMap map) {
        this.boardWidth = map.getMaxWidth();
        this.boardHeight = map.getMaxHeight();
        this.destinations = map.getDestinations();
        this.undoQuota = map.getUndoLimit().orElse(Integer.MAX_VALUE);
        map.getPlayerIds().forEach(id -> entities.put(map.getPlayerPositionById(id), new Player(id)));
        this.destinations.forEach(destination -> entities.putIfAbsent(destination, new Empty()));
    }

    public Position getPlayerPositionById(int id) {
        return entities.entrySet().stream()
                .filter(entry -> entry.getValue() instanceof Player && ((Player) entry.getValue()).getId() == id)
                .map(Map.Entry::getKey)
                .findFirst()
                .orElse(null);
    }

    public Set<Position> getAllPlayerPositions() {
        return entities.entrySet().stream()
                .filter(entry -> entry.getValue() instanceof Player)
                .map(Map.Entry::getKey)
                .collect(Collectors.toSet());
    }

    public Entity getEntity(Position position) {
        return entities.getOrDefault(position, new Empty());
    }

    public Set<Position> getDestinations() {
        return new HashSet<>(destinations);
    }

    public Optional<Integer> getUndoQuota() {
        return undoQuota == Integer.MAX_VALUE ? Optional.empty() : Optional.of(undoQuota);
    }

    public boolean isWin() {
        return destinations.stream().allMatch(dest -> getEntity(dest) instanceof Box);
    }

    public void move(Position from, Position to) {
        Entity entity = entities.remove(from);
        entities.put(to, entity);
        currentTransition.add(from, to);
    }

    public void checkpoint() {
        history.push(currentTransition);
        currentTransition = new Transition();
    }

    public void undo() {
        if (!history.isEmpty()) {
            Transition lastTransition = history.pop();
            lastTransition.reverse().moves.forEach((from, to) -> {
                Entity entity = entities.remove(to);
                entities.put(from, entity);
            });
            if (undoQuota != Integer.MAX_VALUE) {
                undoQuota++;
            }
        }
    }

    public int getMapMaxWidth() {
        return boardWidth;
    }

    public int getMapMaxHeight() {
        return boardHeight;
    }

    private static class Transition {
        private final Map<Position, Position> moves;

        private void add(Position from, Position to) {
            final Position key = this.moves.entrySet().stream()
                    .filter(e -> e.getValue().equals(from))
                    .map(Map.Entry::getKey)
                    .findFirst().orElse(from);
            this.moves.put(key, to);
        }

        private Transition(Map<Position, Position> moves) {
            this.moves = moves;
        }

        private Transition() {
            this.moves = new HashMap<>();
        }

        private Transition reverse() {
            final Map<Position, Position> reversedMoves = this.moves.entrySet().stream()
                    .collect(Collectors.toMap(Map.Entry::getValue, Map.Entry::getKey));
            return new Transition(reversedMoves);
        }

        @Override
        public String toString() {
            final List<String> moves = this.moves.entrySet().stream()
                    .map(e -> String.format("(%d,%d)->(%d,%d)", e.getKey().x(), e.getKey().y(), e.getValue().x(), e.getValue().y()))
                    .toList();
            return String.join(",", moves);
        }
    }
}
