            Transition reverseTransition = lastTransition.reverse();
            reverseTransition.moves.forEach((from, to) -> {
                Entity entity = entities.remove(to);
                entities.put(from, entity);
            });
            if (undoQuota > 0) {
                undoQuota++;
            }
        }
    }

    public int getMapMaxWidth() {
        return boardWidth;
    }

    public int getMapMaxHeight() {
        return boardHeight;
    }

